enum ztCameraType_Enum
{
	ztCameraType_Invalid,
	ztCameraType_Orthographic,
	ztCameraType_Perspective,

	ztCameraType_MAX,
};

// ================================================================================================================================================================================================

struct ztCamera
{
	ztCameraType_Enum type;

	ztVec3 position;

	i32 width, height;
	r32 near_z, far_z;

	ztMat4 mat_view, mat_proj;

	union {
		struct { // orthographic only
			i32 native_w, native_h;
			r32 zoom;
			r32 offset_x, offset_y; // used to determine mouse position when camera isn't full screen
		};

		struct { // perspective only
			r32 fov;
			ztQuat rotation;
			ztVec3 direction;
		};
	};

};

