struct ztFrustum
{
	union {
		struct {
			ztVec3 near_nw;
			ztVec3 near_ne;
			ztVec3 near_se;
			ztVec3 near_sw;
			ztVec3 far_nw;
			ztVec3 far_ne;
			ztVec3 far_se;
			ztVec3 far_sw;
		};

		struct{
			ztVec3 points[8];
		};

		struct {
			ztVec3 near_rect[4];
			ztVec3 far_rect[4];
		};
	};

	union {
		struct {
			ztPlane plane_near;
			ztPlane plane_left;
			ztPlane plane_right;
			ztPlane plane_top;
			ztPlane plane_bottom;
			ztPlane plane_far;
		};

		struct {
			ztPlane planes[6];
		};
	};

	ztFrustum& operator=(const ztFrustum& f);
};

// ================================================================================================================================================================================================

