// ================================================================================================================================================================================================

struct ztDrawCommand
{
	ztDrawCommandType_Enum type;

	union {
		struct {
			char debug[64];
		};

		struct {
			ztVec3 point;
		};

		struct {
			ztVec3 line[2];
		};

		struct {
			ztVec3 tri_pos[3];
			ztVec2 tri_uv[3];
			ztVec3 tri_norm[3];
			ztVec4 tri_color[3];
		};

		struct {
			ztShaderID shader;
			bool       shader_pop;
		};

		struct {
			ztColor color;
			bool    color_pop;
		};

		struct {
			ztTextureID texture[ZT_DRAW_COMMAND_MAX_TEXTURES];
			i32         texture_count;
			bool        texture_pop;
		};

		struct {
			ztVec2         clip_center;
			ztVec2         clip_size;
			int            clip_idx;
			ztDrawCommand *clip_prev;
			bool           clip_pop;
		};

		struct {
			i32 flags;
		};

		struct {
			ztVec3 offset;
			bool   offset_pop;
		};

		struct {
			ztMat4 transform;
			bool   transform_pop;
		};

		struct {
			ztVec3 billboard_center;
			ztVec2 billboard_size;
			ztVec4 billboard_uv;
			ztVec3 billboard_up;
			i32    billboard_flags;
			r32    billboard_rotation;
		};

		struct {
			ztVertexArrayID            vertex_array;
			ztVertexArrayDrawType_Enum vertex_array_draw_type;
		};

		struct {
			ztRendererBlendMode_Enum blend_src;
			ztRendererBlendMode_Enum blend_dest;
			bool                     blend_pop;
		};
	};

	zt_debugOnly(i32 debug_id);
};

