enum ztTweenItemFlags_Enum
{
	ztTweenItemFlags_Loops         = (1 << 0),
	ztTweenItemFlags_PingPongs     = (1 << 1),
	ztTweenItemFlags_Paused        = (1 << 2),
	ztTweenItemFlags_Stopped       = (1 << 3),
	ztTweenItemFlags_DirectionBack = (1 << 4),
};

