struct ztAnimCurve;

// ================================================================================================================================================================================================

struct ztTweenItem
{
	ztTweenItemType_Enum type;

	union {
		struct {
			r32  value_beg;
			r32  value_end;
			r32 *value;
		} real;

		struct {
			ztVec2  value_beg;
			ztVec2  value_end;
			ztVec2 *value;
		} vec2;

		struct {
			ztVec3  value_beg;
			ztVec3  value_end;
			ztVec3 *value;
		} vec3;

		struct {
			ztVec4  value_beg;
			ztVec4  value_end;
			ztVec4 *value;
		} vec4;

		struct {
			ztQuat  value_beg;
			ztQuat  value_end;
			ztQuat *value;
		} quat;
	};

	i32 flags;
	r32 length;
	r32 time;
	r32 delay;

	ZT_FUNCTION_POINTER_VAR(ease_in, ztTweenEase_Func);
	ZT_FUNCTION_POINTER_VAR(ease_out, ztTweenEase_Func);
	ztAnimCurve *curve;
};


void zt_tweenItemMake            (ztTweenItem *tween_item, r32    beg_val, r32    end_val, r32    *value, i32 flags, r32 length, r32 delay, ZT_FUNCTION_POINTER_VAR(ease_in, ztTweenEase_Func), ZT_FUNCTION_POINTER_VAR(ease_out, ztTweenEase_Func));
