void zt_sceneSetSkybox(ztScene *scene, ztModel *skybox)
{
	ZT_PROFILE_RENDERING("zt_sceneSetSkybox");
	zt_returnOnNull(scene);
	zt_returnOnNull(skybox);

	scene->skybox.model = skybox;
}

