			zt_drawListAddEmptyBone(draw_list, ztVec3::zero, 12 * size, 1 * size, 2 * size);
			zt_drawListPushColor(draw_list, zt_bitIsSet(bone->flags, ztBoneFlags_DebugDrawHighlight) ? ztColor_Red : ztColor_Purple);
