ztAnimKey zt_animKeyMake(ztVariant value, r32 time)
{
	ZT_PROFILE_ANIMATION("zt_animKeyMake");
	ztAnimKey anim_key;
	anim_key.value   = value;
	anim_key.time = time;

	return anim_key;
}


// ================================================================================================================================================================================================
